/* eslint-disable no-console */
/* eslint-disable n/no-callback-literal */
import { type FC } from "react";
import { useState, useEffect } from "react";
import { LoadingOutlined, PlusOutlined } from "@ant-design/icons";
import { message, Upload, Image, Button, Form, Input, Space } from "antd";
import type { UploadChangeParam } from "antd/es/upload";
import type { RcFile, UploadFile, UploadProps } from "antd/es/upload/interface";
import { useRequest } from "ahooks";
import { PersonalSettings } from "../../service/api";

const getBase64 = (img: RcFile, callback: (url: string) => void) => {
  const reader = new FileReader();
  reader.addEventListener("load", () => {
    callback(reader.result as string);
  });
  reader.readAsDataURL(img);
};

const beforeUpload = (file: RcFile) => {
  const isJpgOrPng = file.type === "image/jpeg" || file.type === "image/png";
  if (!isJpgOrPng) {
    void message.error("You can only upload JPG/PNG file!");
  }
  const isLt2M = file.size / 1024 / 1024 < 2;
  if (!isLt2M) {
    void message.error("Image must smaller than 2MB!");
  }
  return isJpgOrPng && isLt2M;
};

const Update: FC = () => {
  const { data: GetPersonalData } = useRequest(
    async () => await PersonalSettings()
  );
  console.log(GetPersonalData);

  const [loading, setLoading] = useState(false);
  const [imageUrl, setImageUrl] = useState<string>();
  const handleChange: UploadProps["onChange"] = (
    info: UploadChangeParam<UploadFile>
  ) => {
    if (info.file.status === "uploading") {
      setLoading(true);
      return;
    }
    if (info.file.status === "done") {
      // Get this url from response in real world.
      getBase64(info.file.originFileObj as RcFile, (url) => {
        setLoading(false);
        setImageUrl(url);
      });
    }
  };
  const uploadButton = (
    <div>
      {loading ? <LoadingOutlined /> : <PlusOutlined />}
      <div style={{ marginTop: 8 }}>Upload</div>
    </div>
  );

  // from组件的默认数据
  const [form] = Form.useForm();
  useEffect(() => {
    form.setFieldsValue({
      realName: GetPersonalData?.data.data.realName ?? "",
      mobileNumber: GetPersonalData?.data.data.mobileNumber ?? ""
    });
  }, [GetPersonalData]);
  // 获取from表单默认的值
  const aaa = (value: { realName: string; mobileNumber: string }) => {
    GetPersonalData!.data.data.realName = value.realName;
    GetPersonalData!.data.data.mobileNumber = value.mobileNumber;
  };

  return (
    <>
      <h1 className="text-[20px] mx-[24px] my-[16px]">个人信息设置</h1>
      <div className="w-[600px] mx-[50px]">
        <div>
          <p className="text-[14px] leading-[40px]">头像：</p>
        </div>
        <div>
          <Upload
            name="avatar"
            listType="picture-card"
            className="avatar-uploader"
            showUploadList={false}
            action="https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188"
            beforeUpload={beforeUpload}
            onChange={handleChange}
            iconRender={() => (
              <Image
                width={200}
                height={200}
                src="error"
                fallback="data:image/png;base64,"
              />
            )}
          >
            {imageUrl != null ? (
              <img src={imageUrl} alt="avatar" style={{ width: "100%" }} />
            ) : (
              uploadButton
            )}
          </Upload>
        </div>
        <div className="text-[12px] text-[#999] leading-[18px]">
          <p>上传格式:jpg,jpeg,png,webp</p>
          <p>最大限制2MB</p>
        </div>
      </div>
      <Form
        name="validateOnly"
        layout="vertical"
        autoComplete="off"
        className="mx-[50px] mt-[46px]"
        form={form}
        onFinish={aaa}
      >
        <Form.Item
          name="realName"
          label="真实姓名"
          rules={[{ required: true }]}
        >
          <Input
            className="w-[500px] h-[40px] rounded-[5px]"
            placeholder="请输入真实姓名"
          />
        </Form.Item>
        <Form.Item
          name="mobileNumber"
          label="手机号"
          rules={[{ required: true }]}
        >
          <Input
            className="w-[500px] h-[40px] rounded-[5px]"
            placeholder="请输入手机号"
          />
        </Form.Item>
        <Form.Item>
          <Space>
            <Button
              type="primary"
              htmlType="submit"
              className="h-[40px] px-[15px] rounded-[4px]"
            >
              提交保存
            </Button>
          </Space>
        </Form.Item>
      </Form>
    </>
  );
};

export default Update;
